  // Get the counts of prime and non-prime
  def step2(start: Int, end : Int, primes: Set[Int]): (Int,Int) = {

    (start to end).foldLeft((0,0)){
      case ((prime, notPrime), num) =>

        if(num % 17 == 0) {

          if(primes.contains(num)) {

            (prime + 1, notPrime)
          }
          else {

            (prime, notPrime + 1)
          }

        } else (prime, notPrime)

    }


  }

  def genPrimes(n: Int) : Set[Int] = {

    val start = (2 to n).toSet

    (2 to n).foldLeft(start){
      case (remaining, num) =>
        // remove all numbers evenly divisible by num
        remaining.filter(p => p == num || (p % num != 0))
    }

  }


